function initPagination(){let t=document.getElementsByClassName("flink-num");for(let e=0;e<t.length;e++){let n=t[e].siblings().length;t[e].innerText="（"+(0===n?1:n)+"）"}}Element.prototype.siblings=function(){let t=[],e=this.parentNode.querySelectorAll(".flink_item");for(let n=0;n<e.length;n++)e[n]!==this&&t.push(e[n]);return t},initPagination();