function initPagination(){let t=document.getElementsByClassName("flink-num");for(let n=0;n<t.length;n++){let e=t[n].siblings().length;t[n].innerText="（"+(0===e?1:e)+"）"}}Element.prototype.siblings=function(){let t=[],n=this.parentNode.querySelectorAll(".flink-list-card");for(let e=0;e<n.length;e++)n[e]!==this&&t.push(n[e]);return t},initPagination();