function setTime(t,e){var a=Date.parse(new Date),n=t,s=e,r=Date.parse(new Date(n)),i=Math.abs(parseInt((r-a)/1e3/3600/24/30/12)),o=Math.abs(parseInt((r-a)/1e3/3600/24/30)),p=Math.abs(parseInt((r-a)/1e3/3600/24)),I=Math.abs(parseInt((r-a)/1e3/3600)),h=Math.abs(parseInt((r-a)/1e3/60)),g=Math.abs(parseInt((r-a)/1e3));console.log(p),p>=365?s.text(parseInt(i)+"年"+(o-12*i)+"个月"+(p-(12*i*30+5*i))+"天前").toString():p>=31?s.text(parseInt(o)+"个月"+(p-30*o)+"天前").toString():p>=2?s.text(parseInt(p)+"天前").toString():p>0&&p<2?s.text("昨天").toString():I>0&&I<24?s.text(parseInt(I)+"小时前").toString():h>0&&h<60?s.text(parseInt(h)+"分钟前").toString():g>0&&g<60&&s.text(parseInt(g)+"秒前").toString()}$(".one-comment").each(function(){var t=$(this).find(".time").text(),e=$(this).find(".time");setTime(t,e)});